mapper.select:
SELECT * FROM bundles join categories on categories.id = bundles.category_id
and category_id = #{categoryId}
and bundles.name like concat('%',#{searchContent},'%')

select bundles.id, cart_items.product_type, bundles.name, bundles.image, bundles.price, cart_items.quantity
from bundles
join cart_items on cart_items.product_id = bundles.id
where cart_items.user_id = #{userId}
and bundles.id in

    #{productId}
