mapper.select:
SELECT s.id, shop_category.product_type, s.name, s.price, s.images->>'$[0]' AS image, s.sales FROM shop_bundle s join shop_category on shop_category.id = s.category_id
and category_id = #{categoryId}
and s.name like concat('%',#{searchContent},'%')

select b.id, c.product_type, b.name, b.images->>'$[0]' AS image, b.price, c.quantity from shop_bundle b join shop_cart_item c on c.product_id = b.id where c.user_id = #{userId} and b.id in
#{productId}
and c.product_type = 2

mapper.update:
UPDATE shop_bundle SET stock = stock - CASE
WHEN id = #{bundle.productId} THEN #{bundle.quantity}
ELSE 0 END WHERE id IN
#{bundle.productId}
