mapper.select:
select cv.id, ca.product_type, co.name, cv.variant_name, co.images->>'$[0]' AS image, cv.price, ca.quantity from shop_component_variant cv join shop_component co on cv.component_id = co.id join shop_cart_item ca on ca.product_id = cv.id where ca.user_id = #{userId} and cv.id in
#{productId}
and ca.product_type = 1

mapper.update:
UPDATE shop_component_variant SET stock = stock - CASE
WHEN id = #{component.productId} THEN #{component.quantity}
ELSE 0 END WHERE id IN
#{component.productId}
